import React from 'react';
import PropTypes from "prop-types";
import ListIngredientsStyles from './ListIngredients.module.css';
import {ConstructorElement, DragIcon} from '@ya.praktikum/react-developer-burger-ui-components';

export default function ListIngredients ({ ingredients, bun }) {
    return (
        <div>
            {bun && 
                <ConstructorElement
                    text={bun.name + ' (верх)'}
                    thumbnail={bun.image}
                    price={bun.price}
                    isLocked={true}
                    type="top"
                    extraClass="mt-25 ml-8"
                />
            }
            <ul className={ListIngredientsStyles.listIngredient + ' mb-4 mt-4'}>
                {ingredients &&
                    ingredients.map((item) => (
                        <li style={{ display: 'flex', alignItems: 'center'}} key={item._id}>
                            <DragIcon />
                            <ConstructorElement
                                text={item.name}
                                thumbnail={item.image}
                                price={item.price}
                                extraClass="ml-2"
                            />
                        </li>
                    ))
                }
            </ul>
            { bun &&
                <ConstructorElement
                    text={bun.name + ' (низ)'}
                    thumbnail={bun.image}
                    price={bun.price}
                    isLocked={true}
                    type="bottom"
                    extraClass="ml-8"
                />
            }
        </div>
    )
    }

    const ingredientTypes = {
        _id: PropTypes.string,
        name: PropTypes.string,
        type: PropTypes.string,
        proteings: PropTypes.number,
        fat: PropTypes.number,
        carbohydrates: PropTypes.number,
        calories: PropTypes.number,
        price: PropTypes.number,
        image: PropTypes.string,
        image_mobile: PropTypes.string,
        image_large: PropTypes.string,
        __v: PropTypes.number
    }
    export const listPropTypes = {
        ingredients: PropTypes.arrayOf(
            PropTypes.shape(ingredientTypes)
        ),
        bun: PropTypes.shape(ingredientTypes)
    }

    ListIngredients.propTypes = listPropTypes
