import PropTypes from "prop-types";
import BurgerConstructorStyles from './BurgerConstructor.module.css';
import {ConstructorElement, DragIcon} from '@ya.praktikum/react-developer-burger-ui-components';
import {data} from '../utils/data';

export default function BurgerConstructor ({ ingredients, bun }) {
    const List1 = () => {
    return (
        <div>
            {

                <ConstructorElement
                    text={bun.name + ' (верх)'}
                    thumbnail={bun.image}
                    price={bun.price}
                    isLocked={true}
                    type="top"
                    extraClass="mt-25 ml-8"
                />
            }
            <div className={BurgerConstructorStyles.burgerIngredient + ' mb-4 mt-4'}
            >
                {
                    ingredients &&
                    data.map((el, index) => (
                        <div key={index}>
                            <DragIcon />
                            <ConstructorElement
                                text={el.name}
                                thumbnail={el.image}
                                price={el.price}
                                extraClass="ml-2"
                            />
                        </div>
                    ))
                }
            </div>

            {
                bun &&
                <ConstructorElement
                    text={bun.name + ' (низ)'}
                    thumbnail={bun.image}
                    price={bun.price}
                    isLocked={true}
                    type="bottom"
                    extraClass="ml-8"
                />
            }

        </div>
    )
        }
    return (
        <div className={BurgerConstructorStyles.container}><List1/></div>
    )
}

