import React from 'react';
import {data} from '../utils/data';
import { CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components';





export default function BurgerIngredients  (){


       const List = data.map((item) => {
        if(item.type === "bun") {
        return (
        <div key={item._id}>
            <img src={item.image} alt="" />
            <span>{item.__v}</span>
            <div>
                <span>{item.fat}</span>
                <CurrencyIcon type="primary" />
            </div>
            <p>{item.name}</p>
        </div>
        )
        }
       })
       const List2 = data.map((item) => {
        if(item.type === "sauce") {
        return (
        <div key={item._id}>
            <img src={item.image} alt="" />
            <span>{item.__v}</span>
            <div>
                <span>{item.fat}</span>
                <CurrencyIcon type="primary" />
            </div>
            <p>{item.name}</p>
        </div>
        )
        }
       })
       const List3 = data.map((item) => {
        if(item.type === "main") {
        return (
        <div key={item._id}>
            <img src={item.image} alt="" />
            <span>{item.__v}</span>
            <div>
                <span>{item.fat}</span>
                <CurrencyIcon type="primary" />
            </div>
            <p>{item.name}</p>
        </div>
        )
        }
       })
       return (
        <>
        <div>{List}</div>
        <div>{List2}</div>
        </>
       )

}
