import React, { useRef } from 'react';
import {data} from '../utils/data';
import BurgerIngredientsStyles from './BurgerIngredients.module.css';
import { Tab, CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components';
import { Link, animateScroll as scroll } from "react-scroll";




export default function BurgerIngredients  (){   
    const List1 = () => {
        const myRef = React.createRef();
        const [current, setCurrent] = React.useState('one')
        return (
            <div className={'pt-5 pb-10'} style={{ display: 'flex' }}>
                <Link containerId="containerElement" to="bun" spy={true} smooth={true}>
                    <Tab value="one" active={current === 'one'} onClick={setCurrent}>
                        Булки
                    </Tab>
                </Link>
                <Link containerId="containerElement" to="sauce" spy={true} smooth={true}>
                    <Tab value="two" active={current === 'two'} onClick={setCurrent}>
                        Соусы
                    </Tab>
                </Link>
                <Link containerId="containerElement" to="main" spy={true} smooth={true}>
                    <Tab value="three" active={current === 'three'} onClick={setCurrent}>
                        Начинки
                    </Tab>
                </Link>
            </div>
        )
    }
    const bunList = data.map((item) => {
        if(item.type === "bun") {
            return (
            <div key={item._id} className={BurgerIngredientsStyles.section}>
                <img src={item.image} className={BurgerIngredientsStyles.categories} alt="" />
                <Counter count={item.__v} size={item.__v <= 9 ? 'default' : 'small'}/>
                <div className={BurgerIngredientsStyles.fat + ' pt-1 pb-1'}>
                    <span>{item.fat}</span>
                    <CurrencyIcon type="primary" />
                </div>
                <p>{item.name}</p>
            </div>
            )
        }
    })
    const sauceList = data.map((item) => {

    if(item.type === "sauce") {
    return (
    <div key={item._id} className={BurgerIngredientsStyles.section}>
        <img src={item.image} className={BurgerIngredientsStyles.categories} alt="" />
        <Counter count={item.__v} size={item.__v <= 9 ? 'default' : 'small'} />
        <div className={BurgerIngredientsStyles.fat + ' pt-1 pb-1'}>
            <span>{item.fat}</span>
            <CurrencyIcon type="primary" />
        </div>
        <p>{item.name}</p>
    </div>
    )
    }
    })
    const mainList = data.map((item) => {
    if(item.type === "main") {
    return (
    <div key={item._id} className={BurgerIngredientsStyles.section}>
        <img src={item.image} className={BurgerIngredientsStyles.categories} alt="" />
        <Counter count={item.__v} size={item.__v <= 9 ? 'default' : 'small'} />
        <div className={BurgerIngredientsStyles.fat + ' pt-1 pb-1'}>
            <span>{item.fat}</span>
            <CurrencyIcon type="primary" />
        </div>
        <p>{item.name}</p>
    </div>
    )
    }
    })
    return (
        <div className={BurgerIngredientsStyles.main}>
            <div>
                <h1 className={'pt-10 pb-5 text text_type_main-large'}>Соберите бургер</h1>
                <div><List1/></div>
                <div id="containerElement" className={BurgerIngredientsStyles.burgerIngredient}>

                <h2 id='bun'>Булки</h2>
                <div className={BurgerIngredientsStyles.tabs + ' pt-6 pb-10 pl-4'}>{bunList}</div>
                <h2 id='sauce'>Соусы</h2>
                <div className={BurgerIngredientsStyles.tabs + ' pt-6 pb-10 pl-4'}>{sauceList}</div>
                <h2 id='main'>Начинки</h2>
                <div className={BurgerIngredientsStyles.tabs + ' pt-6 pb-10 pl-4'}>{mainList}</div>
                </div>
            </div>
            <div>

            </div>
        </div>
    )

}
