import PropTypes from "prop-types";
import { useState } from "react";
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';
// import { Ingredients } from "./Ingredients";
// import { ingredientTypes } from "../../utils/types";
import { clsx } from "clsx";

const Ingredients = ({ data, height }) => {
    return (
        <div className={clsx(style.list_container, 'flex column')} style={{ height: height }}>
            {data.map((item, index) => (<ListItem type={item.type} data={item.data} key={index} />))}
        </div >
    )
}

export const listPropTypes = {
    data: PropTypes.arrayOf(
        PropTypes.shape(listItemPropTypes)
    ).isRequired,
    height: PropTypes.number.isRequired
}

Ingredients.propTypes = listPropTypes
export const AppMain = ({ data }) => {
    const bun = data.find(el => el.type === 'bun')
    const ingredients = data.filter(el => el.type !== 'bun')

    return (
        <main className={clsx('flex row justify_content-center gap-10')}>
            <BurgerIngredients data={data} height={500} />
            {/* <BurgerConstructor bun={bun} ingredients={ingredients} /> */}
        </main>
    )
}

export const appMainPropTypes = {
    data: PropTypes.arrayOf(
        PropTypes.shape(ingredientTypes)
    ).isRequired,
}

AppMain.propTypes = appMainPropTypes
 function BurgerIngredients  ({ data, height, className })  {
    const tabs = [
        {
            text: 'Булки',
            value: 'bun'
        },
        {
            text: 'Соусы',
            value: 'sauce'
        },
        {
            text: 'Начинки',
            value: 'main'
        },
    ]
    const [activeTab, setActiveTab] = useState(tabs[0].value)
    const list = Object.entries(
        data.reduce((acc, value) => ({
            ...acc,
            [value.type]: Array.isArray(acc[value.type]) ? [].concat(acc[value.type], value) : [value]

        }), {})
    ).map(([key, value]) => ({
        type: tabs.find(el => el.value === key).text,
        data: value
    }))

    return (
        <div className={clsx(className)} style={{ width: 600 }}>
            <p className="text text_type_main-large mt-10 mb-5">Соберите бургер</p>
            <Tab value={activeTab} tabs={tabs} onClick={setActiveTab} />
            <Ingredients data={list} height={height} />
        </div>
    )
}

export const burgerIngredientsPropTypes = {
    data: PropTypes.arrayOf(
        PropTypes.shape(ingredientTypes)
    ).isRequired,
    height: PropTypes.number.isRequired
}

BurgerIngredients.propTypes = burgerIngredientsPropTypes
