import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Tab, CurrencyIcon, LockIcon, DragIcon, DeleteIcon  } from '@ya.praktikum/react-developer-burger-ui-components';
import BurgerIngredientsStyles from './BurgerIngredients.module.css';
import { Ingredients } from '../utils/data';

//console.log (Ingredients)
export default function BurgerIngredients() {


        const [current, setCurrent] = React.useState('one')
        const ingredientsArray = Ingredients.map(ingredient => 
            <div key={ingredient._id} name={ingredient.name}>

            </div>
            )

        return (

            <section className={BurgerIngredientsStyles.section}>
                <CurrencyIcon type="primary" />
                <LockIcon type="primary" />
                <DragIcon type="primary" />
                <DeleteIcon type="primary" />
                <h1 className={BurgerIngredientsStyles.title + ' pt-10 pb-5 text text_type_main-large'}>
                    Соберите бургер
                </h1>
                    <div style={{ display: 'flex' }}>
                        <Tab value="one" active={current === 'one'} onClick={setCurrent}>
                        Булки
                        </Tab>
                        <Tab value="two" active={current === 'two'} onClick={setCurrent}>
                        Соусы
                        </Tab>
                        <Tab value="three" active={current === 'three'} onClick={setCurrent}>
                        Начинки
                        </Tab>
                    </div>
                <div>
                    <h2>Булки</h2>
                    <div>

                        <div>
                           {ingredientsArray}

                        </div>
                    </div>
                </div>

            </section>
        )
      }
