import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Tab, CurrencyIcon, LockIcon, DragIcon, CloseIcon, CheckMarkIcon, ListIcon, ProfileIcon, EditIcon, InfoIcon 
,ShowIcon, HideIcon, LogoutIcon, DeleteIcon, ArrowUpIcon, ArrowDownIcon, MenuIcon } from '@ya.praktikum/react-developer-burger-ui-components';
import BurgerIngredientsStyles from './BurgerIngredients.module.css';

export default function BurgerIngredients() {


        const [current, setCurrent] = React.useState('one')

        return (

            <section className={BurgerIngredientsStyles.section}>
                <CurrencyIcon type="primary" />
                <LockIcon type="primary" />
                <DragIcon type="primary" />
                <DeleteIcon type="primary" />
                <h1 className={BurgerIngredientsStyles.title + ' pt-10 pb-5 text text_type_main-large'}>
                    Соберите бургер
                </h1>
                <div style={{ display: 'flex' }}>
                    <Tab value="one" active={current === 'one'} onClick={setCurrent}>
                    Булки
                    </Tab>
                    <Tab value="two" active={current === 'two'} onClick={setCurrent}>
                    Соусы
                    </Tab>
                    <Tab value="three" active={current === 'three'} onClick={setCurrent}>
                    Начинки
                    </Tab>
                </div>
                <div>

                </div>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
      <ConstructorElement
        type="top"
        isLocked={true}
        text="Краторная булка N-200i (верх)"
        price={200}
        thumbnail={img}
      />
      <ConstructorElement
        text="Краторная булка N-200i (верх)"
        price={50}
        thumbnail={img}
      />
      <ConstructorElement
        type="bottom"
        isLocked={true}
        text="Краторная булка N-200i (низ)"
        price={200}
        thumbnail={img}
      />
    </div>
            </section>
        )
      }
