import React from 'react';
import {data} from '../utils/data';
import BurgerIngredientsStyles from './BurgerIngredients.module.css';
import { Tab, CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components';

export default function BurgerIngredients  (){   
    const [current, setCurrent] = React.useState('one')
    return (
      <div style={{ display: 'flex' }}>
        <Tab value="one" active={current === 'one'} onClick={setCurrent}>
          One
        </Tab>
        <Tab value="two" active={current === 'two'} onClick={setCurrent}>
          Two
        </Tab>
        <Tab value="three" active={current === 'three'} onClick={setCurrent}>
          Three
        </Tab>
      </div>
    )    
       const List = data.map((item) => {
        if(item.type === "bun") {
        return (
        <div key={item._id} className={BurgerIngredientsStyles.section}>
            <img src={item.image} className={BurgerIngredientsStyles.categories} alt="" />
            <Counter count={item.__v} size="default" />
            <div>
                <span>{item.fat}</span>
                <CurrencyIcon type="primary" />
            </div>
            <p>{item.name}</p>
        </div>
        )
        }
       })
       const List2 = data.map((item) => {
        if(item.type === "sauce") {
        return (
        <div key={item._id} className={BurgerIngredientsStyles.section}>
            <img src={item.image} className={BurgerIngredientsStyles.categories} alt="" />
            <Counter count={item.__v} size="default" />
            <div>
                <span>{item.fat}</span>
                <CurrencyIcon type="primary" />
            </div>
            <p>{item.name}</p>
        </div>
        )
        }
       })
       const List3 = data.map((item) => {
        if(item.type === "main") {
        return (
        <div key={item._id} className={BurgerIngredientsStyles.section}>
            <img src={item.image} className={BurgerIngredientsStyles.categories} alt="" />
            <Counter count={item.__v} size="default" />
            <div>
                <span>{item.fat}</span>
                <CurrencyIcon type="primary" />
            </div>
            <p>{item.name}</p>
        </div>
        )
        }
       })
       return (
        <div className={BurgerIngredientsStyles.burgerIngredient}>

        <h1>Булки</h1>
        <div className={BurgerIngredientsStyles.tabs}>{List}</div>
        <h1>Соусы</h1>
        <div className={BurgerIngredientsStyles.tabs}>{List2}</div>
        <h1>Начинки</h1>
        <div className={BurgerIngredientsStyles.tabs}>{List3}</div>
        </div>
       )

}
